<?phpclass my{    private $host = "localhost";    private $username = "Kerbouchard";    private $password = "1234";    private $dbname = "bdwebapp";    function __construct($q)    {        $this->conn = new mysqli($this->host, $this->username, $this->password, $this->dbname);        $this->conn->set_charset("utf8");        if ($this->conn->connect_error) {            $this->e = "Connection failed: " . $this->conn->connect_error;        }        $res = $this->conn->query($q);        if ($res !== false) {            if (strpos($q, 'SELECT') !== false) {  // Ã¨ case-sensitive                $this->rows = array();                if ($res->num_rows > 0) {                    while ($row = $res->fetch_assoc()) {                        $ob = new stdClass();                        while (list($name, $value) = each($row)) {                            $ob->$name = $value;                        }                        $this->rows[] = $ob;                    }                }                $this->r = count($this->rows);            } else {                $this->r = $this->conn->affected_rows;                $this->last_id = $this->conn->insert_id;            }        } else {            $this->e = $this->conn->error;        }    }    function __destruct()    {        $this->conn->close();    }}?>